import { useEffect, useState } from 'react';
import { getCompetition, updateCompetition } from '../api';
import { TextField, Button, Typography, Grid, MenuItem, FormControl, InputLabel, Select } from '@mui/material';
import countries from '../countries';
import { useNavigate, useParams } from 'react-router-dom';

function padAndFormatWeight(value) {
    if (value.includes('KG')) return value; // Se già formattato
    const [intPart, decPart = "000"] = value.toString().split('.');
    const paddedInt = intPart.padStart(3, '0');
    const paddedDec = decPart.padEnd(3, '0');
    return `${paddedInt},${paddedDec}KG`;
}

function padAndFormatHeight(value) {
    if (value.includes('cm')) return value; // Se già formattato
    const [intPart, decPart = "000"] = value.toString().split('.');
    const paddedInt = intPart.padStart(3, '0');
    const paddedDec = decPart.padEnd(3, '0');
    return `${paddedInt},${paddedDec}cm`;
}

function EditCompetitionForm() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [form, setForm] = useState(null);

    useEffect(() => {
        (async () => {
            const competition = await getCompetition(id);
            setForm(competition);
        })();
    }, [id]);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleNationChange = (player, code) => {
        const country = countries.find(c => c.code === code);
        if (!country) return;
        setForm(f => ({
            ...f,
            [`nazionalita${player}`]: country.label.toUpperCase(),
            [`svg${player}`]: `https://flagcdn.com/${country.code}.svg`
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = { ...form };

        if (data.pesoPL1 && !data.pesoPL1.includes('KG')) data.pesoPL1 = padAndFormatWeight(data.pesoPL1);
        if (data.altezzaPL1 && !data.altezzaPL1.includes('cm')) data.altezzaPL1 = padAndFormatHeight(data.altezzaPL1);
        if (data.pesoPL2 && !data.pesoPL2.includes('KG')) data.pesoPL2 = padAndFormatWeight(data.pesoPL2);
        if (data.altezzaPL2 && !data.altezzaPL2.includes('cm')) data.altezzaPL2 = padAndFormatHeight(data.altezzaPL2);

        await updateCompetition(id, data);
        navigate('/');
    };

    if (!form) return <div>Caricamento...</div>;

    return (
        <form onSubmit={handleSubmit} style={{ marginBottom: '40px', marginTop: '40px' }}>
            <Typography variant="h4" gutterBottom>Modifica Gara: {form.nomeGara}</Typography>
            <Grid container spacing={2}>
                <Grid item xs={12}>
                    <TextField fullWidth label="Nome Gara" name="nomeGara" value={form.nomeGara || ''} onChange={handleChange} required />
                </Grid>

                <Grid item xs={12}>
                    <TextField fullWidth label="Tipo Gara" name="tipoGara" value={form.tipoGara || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12}>
                    <TextField fullWidth label="Nome Campionato" name="nomeCampionato" value={form.nomeCampionato || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12}>
                    <Typography variant="h5">Pugile 1</Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Nome PL1" name="nomePL1" value={form.nomePL1 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Record PL1" name="recordPL1" value={form.recordPL1 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={4}>
                    <TextField fullWidth label="Città PL1" name="cittaPL1" value={form.cittaPL1 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={4}>
                    <FormControl fullWidth>
                        <InputLabel>Nazionalità PL1</InputLabel>
                        <Select
                            value={(form.nazionalitaPL1 || '').toLowerCase()}
                            label="Nazionalità PL1"
                            onChange={(e) => handleNationChange('PL1', e.target.value)}
                        >
                            {countries.map(c => (
                                <MenuItem key={c.code} value={c.code}>{c.label}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Grid>

                <Grid item xs={12} sm={4}>
                    <TextField fullWidth label="Età PL1" name="etàPL1" value={form.etàPL1 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Peso PL1 (000,000KG)" name="pesoPL1" value={form.pesoPL1 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Altezza PL1 (000,000cm)" name="altezzaPL1" value={form.altezzaPL1 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12}>
                    <Typography variant="h5">Pugile 2</Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Nome PL2" name="nomePL2" value={form.nomePL2 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Record PL2" name="recordPL2" value={form.recordPL2 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={4}>
                    <TextField fullWidth label="Città PL2" name="cittaPL2" value={form.cittaPL2 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={4}>
                    <FormControl fullWidth>
                        <InputLabel>Nazionalità PL2</InputLabel>
                        <Select
                            value={(form.nazionalitaPL2 || '').toLowerCase()}
                            label="Nazionalità PL2"
                            onChange={(e) => handleNationChange('PL2', e.target.value)}
                        >
                            {countries.map(c => (
                                <MenuItem key={c.code} value={c.code}>{c.label}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Grid>

                <Grid item xs={12} sm={4}>
                    <TextField fullWidth label="Età PL2" name="etàPL2" value={form.etàPL2 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Peso PL2 (000,000KG)" name="pesoPL2" value={form.pesoPL2 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField fullWidth label="Altezza PL2 (000,000cm)" name="altezzaPL2" value={form.altezzaPL2 || ''} onChange={handleChange} />
                </Grid>

                <Grid item xs={12}>
                    <Button variant="contained" color="primary" type="submit">Salva Modifiche</Button>
                </Grid>
            </Grid>
        </form>
    );
}

export default EditCompetitionForm;
