export async function fetchCompetitions() {
    const response = await fetch('http://localhost:5001/api/competitions');
    return response.json();
}

export async function createCompetition(data) {
    const response = await fetch('http://localhost:5001/api/competitions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
    });
    return response.json();
}

export async function getCompetition(id) {
    const response = await fetch(`http://localhost:5001/api/competitions/${id}`);
    return response.json();
}

export async function updateCompetition(id, data) {
    const response = await fetch(`http://localhost:5001/api/competitions/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
    });
    return response.json();
}

export async function deleteCompetition(id) {
    const response = await fetch(`http://localhost:5001/api/competitions/${id}`, {
        method: 'DELETE'
    });
    return response.json();
}
