import React from 'react';
import { Button, Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import { deleteCompetition } from '../api';
import countries from '../countries';

function getCountryLabel(code) {
    const country = countries.find(c => c.code === code.toLowerCase());
    return country ? country.label : code;
}

function CompetitionList({ competitions, onDeleted }) {

    const handleDelete = async (id) => {
        if (window.confirm("Sei sicuro di voler eliminare questa gara?")) {
            await deleteCompetition(id);
            onDeleted(id);
        }
    };

    return (
        <div style={{ marginBottom: '40px' }}>
            <Typography variant="h4" gutterBottom>Gare</Typography>
            {competitions.length === 0 ? (
                <Typography>Nessuna gara disponibile.</Typography>
            ) : (
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {competitions.map(c => (
                        <li key={c._id} style={{ marginBottom: '10px' }}>
                            <strong>{c.nomeGara}</strong> - {c.tipoGara}
                            {' '}<Link to={`/edit/${c._id}`} style={{ marginRight: '10px', marginLeft: '10px' }}>Modifica</Link>
                            <Button
                                variant="outlined"
                                color="error"
                                onClick={() => handleDelete(c._id)}
                            >
                                Elimina
                            </Button>
                            <Typography variant="body2" color="textSecondary">
                                Peso PL1: {c.pesoPL1} | Altezza PL1: {c.altezzaPL1} | Peso PL2: {c.pesoPL2} | Altezza PL2: {c.altezzaPL2} | Nazionalità PL1: {getCountryLabel(c.nazionalitaPL1)} | Nazionalità PL2: {getCountryLabel(c.nazionalitaPL2)}
                            </Typography>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default CompetitionList;
