                        fullWidth
                        label="Nome PL2"
                        name="nomePL2"
                        value={form.nomePL2 || ''}
                        onChange={handleChange}
                    />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        label="Record PL2"
                        name="recordPL2"
                        value={form.recordPL2 || ''}
