                        fullWidth
                        label="Città PL2"
                        name="cittaPL2"
                        value={form.cittaPL2 || ''}
                        onChange={handleChange}
                    />
                </Grid>

                <Grid item xs={12} sm={4}>
                    <FormControl fullWidth>
                        <InputLabel>Nazionalità PL2</InputLabel>
                        <Select
                            value={(form.nazionalitaPL2 || '').toLowerCase()}
                            label="Nazionalità PL2"
                            onChange={(e) => handleNationChange('PL2', e.target.value)}
                        >
                            {countries.map(c => (
                                <MenuItem key={c.code} value={c.code}>{c.label}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Grid>

                <Grid item xs={12} sm={4}>
                    <TextField
                        fullWidth
                        label="Età PL2"
                        name="etàPL2"
                        value={form.etàPL2 || ''}
                        onChange={handleChange}
                    />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        type="number"
                        label="Peso PL2 (es: 70)"
                        name="pesoPL2"
                        value={form.pesoPL2}
                        onChange={handleChange}
                        inputProps={{ step: "0.001", min: "0" }}
                    />
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                        fullWidth
                        type="number"
                        label="Altezza PL2 (es: 175)"
                        name="altezzaPL2"
                        value={form.altezzaPL2}
                        onChange={handleChange}
                        inputProps={{ step: "0.001", min: "0" }}
                    />
                </Grid>

                <Grid item xs={12}>
                    <Button variant="contained" color="primary" type="submit">Salva Modifiche</Button>
                </Grid>
            </Grid>
        </form>
    );
}

