import React, { useState, useEffect } from 'react';
import { Container, Typography } from '@mui/material';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import CompetitionForm from './components/CompetitionForm';
import SnippetView from './components/SnippetView';
import CompetitionList from './components/CompetitionList';
import EditCompetitionForm from './components/EditCompetitionForm';
import { fetchCompetitions } from './api';

function App() {
    const [competitions, setCompetitions] = useState([]);

    useEffect(() => {
        loadData();
    }, []);

    const loadData = async () => {
        const data = await fetchCompetitions();
        setCompetitions(data);
    };

    const handleCompetitionAdded = () => {
        loadData();
    };

    const handleCompetitionDeleted = (id) => {
        setCompetitions(prev => prev.filter(c => c._id !== id));
    };

    return (
        <Router>
            <Container style={{ marginTop: '40px' }}>
                <Typography variant="h3" gutterBottom>Competitions Wizard</Typography>
                <Routes>
                    <Route path="/" element={
                        <>
                            <CompetitionForm onAdd={handleCompetitionAdded} />
                            <CompetitionList competitions={competitions} onDeleted={handleCompetitionDeleted} />
                            <SnippetView competitions={competitions} />
                        </>
                    } />

                    <Route path="/edit/:id" element={<EditCompetitionForm />} />
                </Routes>
            </Container>
        </Router>
    );
}

export default App;
